2_create_commands.code:
use crate::{database::get_connection, types::{Sector, Stock}};

#[tauri::command]
pub async fn get_sectors() -> Result<Vec<Sector>, String> {
    let pool = get_connection().map_err(|e| e.to_string())?;

    let rows = sqlx::query!("SELECT id, name, symbol FROM sectors")
        .fetch_all(&pool)
        .await
        .map_err(|e| format!("Database error: {}", e))?;

    let sectors = rows.into_iter().map(|row| Sector {
        id: row.id,
        name: row.name,
        symbol: row.symbol,
    }).collect();

    Ok(sectors)
}

#[tauri::command]
pub async fn get_stocks_by_sector(sector_id: i32) -> Result<Vec<Stock>, String> {
    let pool = get_connection().map_err(|e| e.to_string())?;

    let rows = sqlx::query!("SELECT id, symbol, name, sector_id FROM stocks WHERE sector_id = ?", sector_id)
        .fetch_all(&pool)
        .await
        .map_err(|e| format!("Database error: {}", e))?;

    let stocks = rows.into_iter().map(|row| Stock {
        id: row.id,
        symbol: row.symbol,
        name: row.name,
        sector_id: row.sector_id,
    }).collect();

    Ok(stocks)
}

